import React, { useState } from 'react';
//import FileUploader from './components/FileUploader';
import './App.css'; //centers form and other content on page

const App = () => {
  class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
  };
  const [name, setName] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);

  const submitForm = () =>{
    const formData = new FormData();
    formData.append("name", name);
    formData.append("file", selectedFile);

    //axios //this is for uploading your information to a specific address/server/website using axios
      //.post(UPLOAD_URL, formData)
      //.then((res) => {
        //alert("File Upload Success");
     // })
      //.catch((err) => alert("File Upload Error"));
  };

  return (
    <div className="App">
      <form>
        <input 
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)} 
        />

      <input
        type="file"
        value={selectedFile}
        onChange={(e) => setSelectedFile(e.target.files[0])}
        //FileUploader //this is a separate component that I can't get to work
          //onFileSelectSuccess={(file) => setSelectedFile(file)}
          //onFileSelectError={({ error }) => alert(error)}
        />

        <button onClick={submitForm}>Submit</button>
      </form>
    </div>
  );
}

export default App;
